import React, { useEffect, useState } from "react";
import "./Global.css";
import "./App.css";
import "./Sidebar.css";
import "./Main.css";
import api from "./services/api";
//Componente: Bloco de componente isolado HTML, CSS e JS, o qual não interfere no restante da aplicação
//Propriedade: Informações que o componente pai passa para o componente filho
//Estado: Informações mantidas pelo componente (Lembrar: Imutabilidade)

function App() {
  const [github_username, setGithub_username] = useState("");
  const [techs, setTechs] = useState("");
  const [latitude, setLatitude] = useState("");
  const [longitude, setLongitude] = useState("");

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      position => {
        const { latitude, longitude } = position.coords;
        setLatitude(latitude);
        setLongitude(longitude);
      },
      err => {
        console.log(err);
      },
      {
        timeout: 30000
      }
    );
  }, []);

  async function handleAddDev(e) {
    e.preventDefault();

    const response = await api.post("/devs", {
      github_username,
      techs,
      latitude,
      longitude
    });
    console.log(response.data);
  }

  return (
    <div id="app">
      <aside>
        <strong>Cadastrar</strong>
        <form>
          <div className="input-block">
            <label htmlFor="github_username">Usuário do Github</label>
            <input
              name="github_username"
              id="username_github"
              value={github_username}
              onChange={e => setGithub_username(e.target.value)}
              required
            />
          </div>

          <div className="input-block">
            <label htmlFor="techs">Tecnologias</label>
            <input
              name="techs"
              id="techs"
              value={techs}
              onChange={e => setTechs(e.target.value)}
              required
            />
          </div>

          <div className="input-group">
            <div className="input-block">
              <label htmlFor="latitude">Latitude</label>
              <input
                name="latitude"
                id="latitude"
                type="number"
                required
                value={latitude}
                onChange={e => {
                  setLatitude(e.target.value);
                }}
              />
            </div>
            <div className="input-block">
              <label htmlFor="longitude">Longitude</label>
              <input
                name="longitude"
                id="longitude"
                type="number"
                required
                value={longitude}
                onChange={e => {
                  setLongitude(e.target.value);
                }}
              />
            </div>
          </div>
          <button type="submit">Salvar</button>
        </form>
      </aside>
      <main>
        <ul>
          <li className="dev-item">
            <header>
              <img
                src="https://avatars2.githubusercontent.com/u/54036413?v=4"
                alt="Brasileirodev"
              />
              <div className="user-info">
                <strong>Marcel Brasileiro</strong>
                <span>ReactJS, React Native, Node.js</span>
              </div>
            </header>
            <p>
              Programador FULL STACK trabalhando com as tecnologias REACT, REACT
              NATIVE e NODE.js
            </p>
            <a href="https://github.com/brasileirodev/">
              Acessar perfil no Github
            </a>
          </li>
          <li className="dev-item">
            <header>
              <img
                src="https://avatars2.githubusercontent.com/u/54036413?v=4"
                alt="Brasileirodev"
              />
              <div className="user-info">
                <strong>Marcel Brasileiro</strong>
                <span>ReactJS, React Native, Node.js</span>
              </div>
            </header>
            <p>
              Programador FULL STACK trabalhando com as tecnologias REACT, REACT
              NATIVE e NODE.js
            </p>
            <a href="https://github.com/brasileirodev/">
              Acessar perfil no Github
            </a>
          </li>
          <li className="dev-item">
            <header>
              <img
                src="https://avatars2.githubusercontent.com/u/54036413?v=4"
                alt="Brasileirodev"
              />
              <div className="user-info">
                <strong>Marcel Brasileiro</strong>
                <span>ReactJS, React Native, Node.js</span>
              </div>
            </header>
            <p>
              Programador FULL STACK trabalhando com as tecnologias REACT, REACT
              NATIVE e NODE.js
            </p>
            <a href="https://github.com/brasileirodev/">
              Acessar perfil no Github
            </a>
          </li>
          <li className="dev-item">
            <header>
              <img
                src="https://avatars2.githubusercontent.com/u/54036413?v=4"
                alt="Brasileirodev"
              />
              <div className="user-info">
                <strong>Marcel Brasileiro</strong>
                <span>ReactJS, React Native, Node.js</span>
              </div>
            </header>
            <p>
              Programador FULL STACK trabalhando com as tecnologias REACT, REACT
              NATIVE e NODE.js
            </p>
            <a href="https://github.com/brasileirodev/">
              Acessar perfil no Github
            </a>
          </li>
        </ul>
      </main>
    </div>
  );
}

export default App;
